/**
 * Компонент позволяет получить версию и имя пакета iOS-приложения.
 * @category Nonvisual
 */
export default class UFSSystemInfoService {
    /**
     * Метод позволяет получить значение версии iOS-приложения.
     *
     * **Входные параметры:**
     * - `callback` - функция вызывается по итогам работы метода.
     * В качестве параметра ей будет передано значение версии iOS-приложения.
     *
     * **Возвращаемые значения:**
     * отсутствуют
     * @static
     */
    static applicationVersion(callback: (version: string) => void): any;
    /**
     * Метод позволяет получить имя пакета iOS-приложения.
     *
     * **Входные параметры:**
     * - `callback` - функция вызывается по итогам работы метода.
     * В качестве параметра ей будет передано имя пакета iOS-приложения.
     *
     * **Возвращаемые значения:**
     * отсутствуют
     * @static
     */
    static applicationId(callback: (id: string) => void): any;
}
